import matplotlib.pyplot as plt
import numpy as np

def visualize_errors(max_value, n_points, errors1, errors2, segments, using_sigmoid=True, two_times_silu=False, make_absolute=True):
    plot = plt.figure(figsize=(12, 4)) 
    plt.rcParams["font.family"] = "Times New Roman"
    # plt.title("Absolute errors Linear Activation Function Errors", fontsize=18)

    # Generate x values for the plot
    x_values = np.linspace(-max_value, max_value, n_points+1)  # 101 points from -8 to 8 inclusive or -6 to 6 inclusive
    plt.xlim([-max_value, max_value])
    plt.ylim([0, 0.035])

    if make_absolute:
        # Convert errors to absolute values
        errors1 = np.abs(errors1)
        errors2 = np.abs(errors2)
    
    siluAAE = sum(np.abs(errors1)) / len(errors1)
    geluAAE = sum(np.abs(errors2)) / len(errors2)

    colors = ['k', 'green']
    # Plot the Errors
    if using_sigmoid:
        plt.plot(x_values, errors1, label=f'SiLU approx. errors using {segments[0]} non-uniform 1st order segments for sigmoid', linestyle='-', color=colors[0])
        plt.plot(x_values, errors2, label=f'GELU approx. errors using {segments[1]} non-uniform 1st order segments for sigmoid', linestyle='-', color=colors[1])
        #plot the average absolute errors as a horizontal line
        plt.axhline(y=siluAAE, color=colors[0], linestyle='--', label='SiLU Mean Absolute Error (MAE)')
        plt.axhline(y=geluAAE, color=colors[1], linestyle='--', label='GELU Mean Absolute Error (MAE)')
    elif two_times_silu:
        plt.plot(x_values, errors1, label=f'SiLU5a approx. errors using {segments[0]} uniform 1st order segments', linestyle='-', color=colors[0])
        plt.plot(x_values, errors2, label=f'SiLU5b approx. errors using {segments[1]} non-uniform 1st order segments', linestyle='-', color=colors[1])
        #plot the average absolute errors as a horizontal line
        plt.axhline(y=siluAAE, color=colors[0], linestyle='--', label='SiLU5a Mean Absolute Error (MAE)')
        plt.axhline(y=geluAAE, color=colors[1], linestyle='--', label='SiLU5b Mean Absolute Error (MAE)')
        plt.axhline(y=0.0078125, color='blue', linestyle='--',  label='BF16: 1 ULP')

    # Add labels and title
    plt.xlabel('Input Range (x)', fontdict={'size': 12})
    plt.ylabel('Absolute Error', fontdict={'size': 12})
    # if make_absolute:
    #     plt.title('Absolute SiLU Errors for x*sigmoid(x) where sigmoid is approximated using 18 versus 20 PWL segments')
    # else:
    #     plt.title('SiLU Errors for x*sigmoid(x) where sigmoid is approximated using 18 versus 20 PWL segments')
    plt.legend(fontsize=12)
    plt.tight_layout()
    # Show the plot
    plt.show()

def visualize_errors_silu(max_value, n_points, errors1, segments, firstOrder=True, make_absolute=True):
    plot = plt.figure(figsize=(12, 4)) 
    plt.rcParams["font.family"] = "Times New Roman"
    # plt.title("Absolute errors Linear Activation Function Errors", fontsize=18)

    # Generate x values for the plot
    x_values = np.linspace(-max_value, max_value, n_points+1)  # 101 points from -8 to 8 inclusive or -6 to 6 inclusive
    plt.xlim([-max_value, max_value])
    plt.ylim([0, 0.05])

    if make_absolute:
        # Convert errors to absolute values
        errors1 = np.abs(errors1)
    
    siluAAE = sum(np.abs(errors1)) / len(errors1)

    colors = ['k', 'grey']
    # Plot the Errors
    if firstOrder:
        plt.plot(x_values, errors1, label=f'SiLU approx. errors using {segments} uniform 1st order segments for SiLU', linestyle='-', color=colors[0])
    else:
        plt.plot(x_values, errors1, label=f'SiLU approx. errors using {segments} uniform zero-order segments for SiLU', linestyle='-', color=colors[1])

    #plot the average absolute errors as a horizontal line
    plt.axhline(y=siluAAE, color=colors[0], linestyle='--', label='SiLU Mean Absolute Error (MAE)')

    # Add labels and title
    plt.xlabel('Input Range (x)', fontdict={'size': 12})
    plt.ylabel('Absolute Error', fontdict={'size': 12})
    # if make_absolute:
    #     plt.title('Absolute SiLU Errors for x*sigmoid(x) where sigmoid is approximated using 18 versus 20 PWL segments')
    # else:
    #     plt.title('SiLU Errors for x*sigmoid(x) where sigmoid is approximated using 18 versus 20 PWL segments')
    plt.legend()
    plt.tight_layout()
    # Show the plot
    plt.show()

